mapper.select:
/* HUB 센터 소식 리스트 조회 */ SELECT HUB_PAGE.HUB_SN , HUB_PAGE.HUB_TTL , HUB_PAGE.HUB_CN , HUB_PAGE.ATCH_FILE_SN1 , HUB_PAGE.HUB_PATH_IMG , HUB_PAGE.HUB_IMG_NAME , HUB_PAGE.ATCH_FILE_SN3 , HUB_PAGE.HUB_PATH , HUB_PAGE.HUB_FILE_NAME , HUB_PAGE.FRST_REG_DT , HUB_PAGE.CTGRY FROM ( /* 순번 매기기 */ SELECT ROWNUM ROWNUMBER , HUB.* FROM ( /* 리스트 조회 */ SELECT HUB.HUB_SN AS HUB_SN , HUB.HUB_TTL AS HUB_TTL , HUB.HUB_CN AS HUB_CN , HUB.ATCH_FILE_SN1 AS ATCH_FILE_SN1 , REPLACE(FILES1.FILE_PATH, '\', '/') AS HUB_PATH_IMG , FILES1.FILE_NM AS HUB_IMG_NAME , HUB.ATCH_FILE_SN2 AS ATCH_FILE_SN2 , REPLACE(FILES2.FILE_PATH, '\', '/') AS HUB_PATH_MOV , FILES2.FILE_NM AS HUB_MOV_NAME , HUB.ATCH_FILE_SN3 AS ATCH_FILE_SN3 , REPLACE(FILES3.FILE_PATH, '\', '/') AS HUB_PATH , FILES3.FILE_NM AS HUB_FILE_NAME , TO_CHAR(HUB.FRST_REG_DT, 'YYYY.MM.DD') AS FRST_REG_DT , HUB.CTGRY AS CTGRY FROM KB_HUB_DAY_INFO HUB , KB_FILE_INFO FILES1 , KB_FILE_INFO FILES2 , KB_FILE_INFO FILES3 WHERE HUB.ATCH_FILE_SN1 = FILES1.FILE_SN(+) AND HUB.ATCH_FILE_SN2 = FILES2.FILE_SN(+) AND HUB.ATCH_FILE_SN3 = FILES3.FILE_SN(+) AND HUB.EXPSR_YN = 'Y' ORDER BY HUB.SORT_NO, HUB.HUB_SN DESC ) HUB WHERE 1=1
AND HUB.HUB_TTL LIKE '%'|| UPPER(#{keyword}) ||'%' OR HUB.HUB_CN LIKE '%'|| UPPER(#{keyword}) ||'%'
AND HUB.HUB_TTL LIKE '%'|| UPPER(#{keyword}) ||'%'
AND HUB.HUB_CN LIKE '%'|| UPPER(#{keyword}) ||'%'
) HUB_PAGE WHERE ROWNUMBER BETWEEN #{start} AND #{end}
