mapper.select:
/* 공지사항 리스트 조회 */ SELECT NOTICE_PAGE.* FROM ( /* 순번 매기기 */ SELECT ROWNUM ROWNUMBER , NOTICE.* FROM ( /* 리스트 조회 */ SELECT NTC.* FROM ( SELECT NTC.NTC_SN AS NTC_SN , NTC.NTC_TTL AS NTC_TTL , NTC.NTC_CN AS NTC_CN , TO_CHAR(NTC.FRST_REG_DT, 'YYYY.MM.DD') AS FRST_REG_DT , NTC.NTC_YN AS NTC_YN , FILES.FILE_SN AS ATCH_FILE_SN , FILES.ORGNL_FILE_NM AS ORIGIN_FILE_NAME , FILES.FILE_NM AS NTC_FILE_NAME , FILES.FILE_PATH AS NTC_PATH FROM KB_NTC_INFO NTC , KB_FILE_INFO FILES WHERE NTC.NTC_YN = 'Y' AND NTC.EXPSR_YN = 'Y' AND NTC.ATCH_FILE_SN = FILES.FILE_SN(+) ORDER BY NTC.SORT_NO, NTC.NTC_SN DESC ) NTC UNION ALL SELECT NTC.* FROM ( SELECT NTC.NTC_SN AS NTC_SN , NTC.NTC_TTL AS NTC_TTL , NTC.NTC_CN AS NTC_CN , TO_CHAR(NTC.FRST_REG_DT, 'YYYY.MM.DD') AS FRST_REG_DT , NTC.NTC_YN AS NTC_YN , FILES.FILE_SN AS ATCH_FILE_SN , FILES.ORGNL_FILE_NM AS ORIGIN_FILE_NAME , FILES.FILE_NM AS NTC_FILE_NAME , FILES.FILE_PATH AS NTC_PATH FROM KB_NTC_INFO NTC , KB_FILE_INFO FILES WHERE NTC_YN = 'N' AND NTC.EXPSR_YN = 'Y' AND NTC.ATCH_FILE_SN = FILES.FILE_SN(+) ORDER BY NTC.SORT_NO, NTC.NTC_SN DESC ) NTC ) NOTICE WHERE 1=1
AND NOTICE.NTC_TTL LIKE '%'|| #{keyword} ||'%' OR NOTICE.NTC_CN LIKE '%'|| #{keyword} ||'%'
AND NOTICE.NTC_TTL LIKE '%'|| #{keyword} ||'%'
AND NOTICE.NTC_CN LIKE '%'|| #{keyword} ||'%'
) NOTICE_PAGE WHERE ROWNUMBER BETWEEN #{start} AND #{end}
